    private void VerTablaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_VerTablaActionPerformed
        try {
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/base_david", "root", "");
            PreparedStatement pst = cn.prepareStatement("SELECT * FROM obligacion");
            ResultSet rs = pst.executeQuery();

            // Crear el modelo de la tabla con las columnas adecuadas
            DefaultTableModel modelo = new DefaultTableModel();
            modelo.addColumn("Id Obligacion");
            modelo.addColumn("Descripcion");
            modelo.addColumn("Monto");
            modelo.addColumn("Fecha Vencimiento");

            // Llenar el modelo con los datos de la consulta
            while (rs.next()) {
                Object[] fila = new Object[5];
                fila[0] = rs.getString("Id_obligacion");
                fila[1] = rs.getString("Descripción");
                fila[2] = rs.getString("Monto");
                fila[3] = rs.getString("Fecha_vencimiento");
                modelo.addRow(fila);
            }

            // Asignar el modelo a la JTable
            jTable2.setModel(modelo);
        } catch (SQLException e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_VerTablaActionPerformed

