    private void AñadirObligacionActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AñadirObligacionActionPerformed
        try {
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/base_david", "root", "");
            PreparedStatement pst = cn.prepareStatement("INSERT INTO personas_negocios (Numero_Doc, Documento, Id_Negocio) VALUES (?, ?, ?)");
            pst.setString(1, NumID2.getText().trim());      // Número de documento
            pst.setString(2, (String) (TipoDoc.getSelectedItem()));  // Tipo de documento
            pst.setInt(3, Integer.parseInt(Id_Fidu.getText().trim()));         // ID del negocio fiduciario
            // Ejecutar la sentencia SQL
            int filasAfectadas = pst.executeUpdate();
            // Comprobar si la inserción fue exitosa
                if (filasAfectadas > 0) {
                    System.out.println("Insercion exitosa en personas_negocios.");
                    Estado_Añadir2.setText(("Insercion exitosa"));
                } else {
                    System.out.println("No se pudo insertar el registro.");
                    Estado_Añadir2.setText(("Insercion fallida"));
                }

        } catch (Exception e) {
            e.printStackTrace();
        }


    }//GEN-LAST:event_AñadirObligacionActionPerformed

