    private void GuardarExcelActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_GuardarExcelActionPerformed
        try {
            Connection cn = DriverManager.getConnection("jdbc:mysql://localhost/base_david", "root", "");
            PreparedStatement pst = cn.prepareStatement("SELECT "
                + "p.Nombre, "
                + "p.Apellido, "
                + "p.Tipo_Documento, "
                + "p.Numero_Documento, "
                + "n.Id_Negocio_Fiduciario, "
                + "n.Nombre AS Negocio_Nombre, "
                + "n.Descripción, "
                + "n.Fecha_inicio, "
                + "n.Fecha_fin, "
                + "o.Id_obligacion, "
                + "o.Descripción AS Obligacion_Descripcion, "
                + "o.Monto AS Obligacion_Monto, "
                + "o.Fecha_vencimiento AS Obligacion_Fecha_Vencimiento "
                + "FROM personas_participantes p "
                + "JOIN personas_negocios pn ON p.Numero_Documento = pn.Numero_Doc AND p.Tipo_Documento = pn.Documento "
                + "JOIN negocio_obligacion n_o ON pn.Id_Negocio = n_o.Id_Negocio "
                + "JOIN obligacion o ON n_o.obligacion_ID = o.Id_obligacion "
                + "JOIN negocio_fiduciario n ON n_o.Id_Negocio = n.Id_Negocio_Fiduciario "
                + "WHERE p.Numero_Documento = ? AND p.Tipo_Documento = ?;"
            );
            
    
            pst.setString(1, GuardarNum.getText());
            pst.setString(2, (String) (GuardarDoc.getSelectedItem()));
            ResultSet rs = pst.executeQuery();
            System.out.println(rs);
 
             
            String nombreArchivo = "Resumen_Obligaciones.csv";
            File file = new File(nombreArchivo);
            PrintWriter writer = new PrintWriter(file, "UTF-8");

            // Escribir datos de la consulta
             writer.println("Nombre;Apellido;Tipo Documento;Número Documento;Id Negocio Fiduciario;Negocio Nombre;Descripción Negocio;Fecha Inicio;Fecha Fin;Id Obligación;Descripción Obligación;Monto;Fecha Vencimiento");

            // Escribir datos
            while (rs.next()) {
                writer.println(
                    rs.getString("Nombre") + ";" +
                    rs.getString("Apellido") + ";" +
                    rs.getString("Tipo_Documento") + ";" +
                    rs.getString("Numero_Documento") + ";" +
                    rs.getInt("Id_Negocio_Fiduciario") + ";" +
                    rs.getString("Negocio_Nombre") + ";" +
                    rs.getString("Descripción") + ";" +
                    rs.getDate("Fecha_inicio") + ";" +
                    rs.getDate("Fecha_fin") + ";" +
                    rs.getInt("Id_obligacion") + ";" +
                    rs.getString("Obligacion_Descripcion") + ";" +
                    rs.getDouble("Obligacion_Monto") + ";" +
                    rs.getDate("Obligacion_Fecha_Vencimiento")
                );
            }

            writer.close();
            rs.close();
            pst.close();
            cn.close();
           
            System.out.println("Archivo Excel generado exitosamente sin contraseña.");


        } catch (Exception e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_GuardarExcelActionPerformed

